<?php	session_start();	require_once 'db_connect.php';	if($_SERVER['REQUEST_METHOD'] == "POST") {		// SQL query		$query = mysql_query("SELECT user_pass FROM tbl_users WHERE user_login='".mysql_real_escape_string($_POST['login'])."' LIMIT 1");		$result = mysql_fetch_assoc($query);		if ($result['user_pass'] === md5($_POST['password'])) {			$_SESSION['auth'] = true;			$response = array(				'status' => 'OK',				'msg' => 'Авторизация прошла успешно',				'redirect' => '/projects'			);		} else {			$response = array(				'status' => 'FAIL',				'msg' => 'Логин и пароль не совпадают'			);		}		echo json_encode($response);	} else {		header("HTTP/1.1 302 Moved Temporarily");		header("Location: /");		exit;	}