<?php	session_start();	if($_SERVER['REQUEST_METHOD'] == "POST") {		if ($_POST['login'] === 'admin' && md5($_POST['password']) === md5('admin')) {			$_SESSION['auth'] = true;			$response = array('status' => 'OK', 'redirect' => '/projects.php');		} else {			$response = array('status' => 'FAIL', 'msg' => 'Логин и пароль не совпадают');		}		echo json_encode($response);	} else {		header("HTTP/1.1 302 Moved Temporarily");		header("Location: /index.php");		exit;	}