<?php	if($_SERVER['REQUEST_METHOD'] == "POST") {		$file = $_FILES['upload'];		$file_name = $file['name'];		$file_dir = dirname(__DIR__).'/img/uploads/'.$file_name;		$file_ext = pathinfo($file_name, PATHINFO_EXTENSION);		$allowed = array('gif','png','jpg','jpeg');		if(!in_array($file_ext, $allowed)) {			$response = array('status' => 'FAIL', 'msg' => 'Некорректный формат. Загрузите jpg, png или gif');		}		else if(move_uploaded_file($file['tmp_name'], $file_dir)){			$response = array('status' => 'OK', 'msg' => 'Файл успешно загружен на сервер');		} 		else {			$response = array('status' => 'FAIL', 'msg' => 'Возникла ошибка при загрузке файла на сервер');		}		echo json_encode($response);	} else {		header("HTTP/1.1 302 Moved Temporarily");		header("Location: /index.php");		exit;	}